var Ansible = require('./ansible');

var util = require('util');

var command = new Ansible.Playbook().playbook("./ansible/playbooks/runscript");

command.privateKey("./ansible/keys/ansible-test.pem")

command.inventory('35.164.240.34,')
command.user('centos')

command.variables({outFile: 'test.out'})

command.on('stdout', function(data) { console.log("stdout:",data.toString()); });
command.on('stderr', function(data) { console.log("stderr",data.toString()); });

command.exec();