 var ansibleClient = require('../').ansibleClient();
 var logger = require('log4js').getLogger();

logger.setLevel('DEBUG');



 ansibleClient.executePlaybook(require('local/test_data.js').ansible_file_test_x, function (err, commandOut) {
     if (err) {
         logger.error('Error executing playbook:', err.message)
         return
     }
     logger.info('commandOut:', JSON.stringify(commandOut));  
 })