const NodeCache = require( "node-cache" );
const myCache = new NodeCache( { stdTTL: 100, checkperiod: 5 } );


obj = { my: "Special", variable: 42 };
myCache.set( "myKey", obj,10,function( err, success ){
  if( !err && success ){
    console.log( success );
    // true 
    // ... do something ... 
  }
});

myCache.on( "expired", function( key, value ){
 console.log('Expired:',key,value)   
});

console.log('Cache mykey BEFORE :',myCache.get( "myKey" ));
setTimeout(function() {
  console.log('Cache mykey AFTER:',myCache.get( "myKey" ));
}, 50000);