var publisher = require('../').streamPublisher();
var streamConsumer = require('../').streamConsumer;

var topic = 'stack-wf-events'
publisher.init({
    zookeeper: 'kafka.stackdc.io:2181'
}, function (err, status) {
    if (status) {
        publisher.createTopic(topic, 
                function (err, res) {
                    console.log('Result:' + res);
                    console.log('error:' + err);
                })
    }
});


