 var sshClient = require('modules/ssh/sshClient')();
 var logger = require('log4js').getLogger();

 var execScriptInfo = require('local/test_data.js').ssh_test_sudo

logger.setLevel('DEBUG');


 sshClient.connect(execScriptInfo, function (err, conn) {
     if (err) {
         logger.error('Error Connecting to host:', err.message)
         return
     }
     logger.debug('Connection Status: Sucess')
     if (conn) {
         sshClient.executeScript(execScriptInfo, function (err, commandOut) {
             if (err) {
                 logger.error('Error executing script on Host:', execScriptInfo.host, err.message)
                 return;
             }
             logger.info('commandOut:', JSON.stringify(commandOut));
             sshClient.disconnect();
         });

     }


 })